const { Client, Intents } = require('discord.js');
const client = new Client({ intents: [Intents.FLAGS.GUILDS, Intents.FLAGS.GUILD_MESSAGES] });

client.once('ready', () => {
  console.log('Ready!');
});

client.on('interactionCreate', async interaction => {
  if (!interaction.isCommand()) return;

  const { commandName } = interaction;

  if (commandName === 'ban') {
    if (!interaction.member.permissions.has('BAN_MEMBERS')) {
      return interaction.reply({ content: 'You don\'t have the permission to ban members!', ephemeral: true });
    }

    const member = interaction.options.getMember('user');
    if (!member) {
      return interaction.reply({ content: 'Please specify a user to ban!', ephemeral: true });
    }

    await member.ban({ reason: 'Banned by the bot' });
    interaction.reply({ content: `Successfully banned ${member.user.username}!`, ephemeral: true });
  }
});

client.login('YOUR_BOT_TOKEN_HERE');
```
