  // Add a plugin to capture screenshots on failure
  on('after:run', (results) => {
    results.forEach((run) => {
      if (run.stats.failures > 0) {
        run.screenshots.forEach((screenshot) => {
          cy.task('saveScreenshot', screenshot);
        });
      }
    });
