    saveScreenshot(screenshot) {
      const screenshotPath = `${config.screenshotFolder}/${screenshot.name}`;
      cy.writeFile(screenshotPath, screenshot.data);
      console.log(`Screenshot saved to ${screenshotPath}`);
    }
  });

  return config;
};
