```c++
#include <iostream>
#include <fstream>
#include <string>
#include <unordered_map> // For efficient word counting

using namespace std;

int countWordOccurrences(const string& filename, const string& targetWord) {
  // 1. Open the file for reading
  ifstream file(filename);
  if (!file.is_open()) {
    cerr << "Error opening file: " << filename << endl;
    return -1; // Indicate failure
  }

  // 2. Use an unordered_map for fast word counting
  unordered_map<string, int> wordCounts;

  // 3. Read and process each line
  string line;
  while (getline(file, line)) {
    // 3.1 Tokenize the line (assuming space as delimiter)
    string word;
    for (const auto& token : split(line, ' ')) { //  'split' helper for clarity
      word = token;
      // 3.2 Case-insensitive counting (optional, adjust as needed)
      transform(word.begin(), word.end(), word.begin(), ::tolower);
      wordCounts[word]++;
    }
  }

  // 4. Close the file
  file.close();

  // 5. Return the count for the target word
  return wordCounts[targetWord];
}

// Helper function for line tokenization (simplified)
