#include <iostream>

void findCommonElements(int arr1[], int arr2[], int n1, int n2) {
  // 1. Sort both arrays (essential for in-place without extra DS)
  std::sort(arr1, arr1 + n1);
  std::sort(arr2, arr2 + n2);

  // 2. Use two pointers, one for each sorted array
  int i = 0, j = 0;

  // 3. Iterate through the sorted arrays, comparing elements
  while (i < n1 && j < n2) {
    if (arr1[i] == arr2[j]) {
      // Common element found, print or process it (here, we'll mark)
      arr1[i] = arr1[i] * 2;  // Example: Marking (adjust as needed)
      i++;
      j++;
    } else if (arr1[i] < arr2[j]) {
      // Move pointer for smaller element (arr1 in this case)
      i++;
    } else {
      // Move pointer for smaller element (arr2)
      j++;
    }
  }
}

int main() {
  int arr1[] = {2, 5, 7, 8, 11, 12};
  int arr2[] = {5, 8, 12, 15, 20};
  int n1 = sizeof(arr1) / sizeof(arr1[0]);
  int n2 = sizeof(arr2) / sizeof(arr2[0]);

