#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>
#include <stdio.h>
#include <stdlib.h>

// Define constants
#define SCREEN_WIDTH 640
#define SCREEN_HEIGHT 480
#define BLOCK_SIZE 20
#define FPS 60

// Define colors
SDL_Color white = {255, 255, 255};
SDL_Color black = {0, 0, 0};

// Define snake and food structures
typedef struct {
    int x, y;
} Snake;

typedef struct {
    int x, y;
} Food;

// Function to initialize SDL
void initSDL() {
    if (SDL_Init(SDL_INIT_VIDEO) < 0) {
        printf("SDL initialization failed: %s\n", SDL_GetError());
        exit(1);
    }
}

// Function to load image
SDL_Texture* loadImage(const char* filename) {
    SDL_Surface* surface = IMG_Load(filename);
    if (!surface) {
        printf("Failed to load image: %s\n", SDL_GetError());
        exit(1);
    }
    SDL_Texture* texture = SDL_CreateTextureFromSurface(SDL_GetRenderer(SDL_GetWindowFromID(SDL_GetWindowID(NULL))), surface);
    SDL_FreeSurface(surface);
    return texture;
}

// Function to draw rectangle
void drawRectangle(SDL_Renderer* renderer, int x, int y, int w, int h, SDL_Color color) {
    SDL_Rect rect = {x, y, w, h};
    SDL_SetRenderDrawColor(renderer, color.r, color.g, color.b, color.a);
    SDL_RenderFillRect(renderer, &rect);
}

// Function to draw snake
void drawSnake(SDL_Renderer* renderer, Snake* snake, int length) {
    for (int i = 0; i < length; i++) {
        drawRectangle(renderer, snake[i].x * BLOCK_SIZE, snake[i].y * BLOCK_SIZE, BLOCK_SIZE, BLOCK_SIZE, white);
    }
}

// Function to draw food
void drawFood(SDL_Renderer* renderer, Food food) {
    drawRectangle(renderer, food.x * BLOCK_SIZE, food.y * BLOCK_SIZE, BLOCK_SIZE, BLOCK_SIZE, white);
}

// Function to handle events
void handleEvents(SDL_Event* event) {
    if (event->type == SDL_QUIT) {
        SDL_Quit();
        exit(0);
    }
}

// Function to update game state
void updateGameState(Snake* snake, Food* food, int* length) {
    // Update snake position
    snake[(*length) - 1].x = snake[(*length) - 2].x;
    snake[(*length) - 1].y = snake[(*length) - 2].y;

    // Move snake
    if (SDL_GetKeyState(NULL)[SDL_SCANCODE_UP] && snake[(*length) - 1].y > 0) {
        snake[(*length) - 1].y--;
    } else if (SDL_GetKeyState(NULL)[SDL_SCANCODE_DOWN] && snake[(*length) - 1].y < SCREEN_HEIGHT / BLOCK_SIZE - 1) {
        snake[(*length) - 1].y++;
    } else if (SDL_GetKeyState(NULL)[SDL_SCANCODE_LEFT] && snake[(*length) - 1].x > 0) {
        snake[(*length) - 1].x--;
    } else if (SDL_GetKeyState(NULL)[SDL_SCANCODE_RIGHT] && snake[(*length) - 1].x < SCREEN_WIDTH / BLOCK_SIZE - 1) {
        snake[(*length) - 1].x++;
    }

    // Check collision with food
    if (snake[(*length) - 1].x == food->x && snake[(*length) - 1].y == food->y) {
        // Increase snake length
        (*length)++;
        // Generate new food
        food->x = rand() % (SCREEN_WIDTH / BLOCK_SIZE);
        food->y = rand() % (SCREEN_HEIGHT / BLOCK_SIZE);
    }
}

// Function to render game
void renderGame(SDL_Renderer* renderer, Snake* snake, Food food, int length) {
    SDL_SetRenderDrawColor(renderer, black.r, black.g, black.b, black.a);
    SDL_RenderClear(renderer);

    drawSnake(renderer, snake, length);
    drawFood(renderer, food);

    SDL_RenderPresent(renderer);
}

int main() {
    // Initialize SDL
    initSDL();

    // Create window
    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    if (!window) {
        printf("Failed to create window: %s\n", SDL_GetError());
        exit(1);
    }

    // Create renderer
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    if (!renderer) {
        printf("Failed to create renderer: %s\n", SDL_GetError());
        exit(1);
    }

    // Load images
    SDL_Texture* background = loadImage("background.png");
    SDL_Texture* snakeHead = loadImage("snake_head.png");
    SDL_Texture* snakeBody = loadImage("snake_body.png");
    SDL_Texture* food = loadImage("food.png");

    // Initialize snake and food
    Snake snake[100];
    Food food;
    food.x = rand() % (SCREEN_WIDTH / BLOCK_SIZE);
    food.y = rand() % (SCREEN_HEIGHT / BLOCK_SIZE);
    int length = 1;
    snake[0].x = SCREEN_WIDTH / 2 / BLOCK_SIZE;
    snake[0].y = SCREEN_HEIGHT / 2 / BLOCK_SIZE;

    // Main loop
    SDL_Event event;
    int running = 1;
    while (running) {
        while (SDL_PollEvent(&event)) {
            handleEvents(&event);
        }

        // Update game state
        updateGameState(snake, &food, &length);

        // Render game
        renderGame(renderer, snake, food, length);

        // Cap framerate
        SDL_Delay(1000 / FPS);
    }

    // Clean up
    SDL_DestroyTexture(background);
    SDL_DestroyTexture(snakeHead);
    SDL_DestroyTexture(snakeBody);
    SDL_DestroyTexture(food);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();

    return 0;
}
```
