#include <SDL2/SDL_image.h>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>

// Define constants
#define SCREEN_WIDTH 800
#define SCREEN_HEIGHT 600
#define BLOCK_SIZE 20
#define FPS 60

// Define colors
SDL_Color white = { 255, 255, 255 };
SDL_Color black = { 0, 0, 0 };

// Define snake and food structures
typedef struct {
    int x, y;
} Snake;
typedef struct {
    int x, y;
} Food;

