// Function to load image
SDL_Texture* load_image(const char* filename) {
    SDL_Surface* surface = IMG_Load(filename);
    if (surface == NULL) {
        printf("Unable to load image %s! SDL_image Error: %s\n", filename, IMG_GetError());
        exit(1);
    }
