// Function to draw rectangle
void draw_rectangle(SDL_Renderer* renderer, int x, int y, int w, int h, SDL_Color color) {
    SDL_Rect rect = { x, y, w, h };
    SDL_SetRenderDrawColor(renderer, color.r, color.g, color.b, color.a);
    SDL_RenderFillRect(renderer, &rect);
}

// Function to draw text
void draw_text(SDL_Renderer* renderer, const char* text, int x, int y, SDL_Color color) {
    SDL_Surface* surface = TTF_RenderText_Solid(font, text, color);
    SDL_Texture* texture = SDL_CreateTextureFromSurface(renderer, surface);
    SDL_Rect rect = { x, y, surface->w, surface->h };
    SDL_RenderCopy(renderer, texture, NULL, &rect);
    SDL_FreeSurface(surface);
    SDL_DestroyTexture(texture);
}

// Function to draw snake
void draw_snake(SDL_Renderer* renderer, Snake* snake, int length) {
    for (int i = 0; i < length; i++) {
        draw_rectangle(renderer, snake[i].x * BLOCK_SIZE, snake[i].y * BLOCK_SIZE, BLOCK_SIZE, BLOCK_SIZE, white);
    }
}

// Function to draw food
void draw_food(SDL_Renderer* renderer, Food food) {
    draw_rectangle(renderer, food.x * BLOCK_SIZE, food.y * BLOCK_SIZE, BLOCK_SIZE, BLOCK_SIZE, white);
}

// Function to handle events
void handle_events(SDL_Event* event, Snake* snake, Food* food, int* length, int* direction) {
    if (event->type == SDL_QUIT) {
        exit(0);
    } else if (event->type == SDL_KEYDOWN) {
        if (event->key.keysym.sym == SDLK_UP && *direction != 2) {
            *direction = 0;
        } else if (event->key.keysym.sym == SDLK_DOWN && *direction != 0) {
            *direction = 2;
        } else if (event->key.keysym.sym == SDLK_LEFT && *direction != 1) {
            *direction = 3;
        } else if (event->key.keysym.sym == SDLK_RIGHT && *direction != 3) {
            *direction = 1;
        }
    }
}

// Function to update snake position
void update_snake(Snake* snake, int* length, int direction) {
    for (int i = *length - 1; i > 0; i--) {
        snake[i].x = snake[i - 1].x;
        snake[i].y = snake[i - 1].y;
    }
    switch (direction) {
        case 0:
            snake[0].y--;
            break;
        case 1:
            snake[0].x++;
            break;
        case 2:
            snake[0].y++;
            break;
        case 3:
            snake[0].x--;
            break;
    }
}

// Function to check collision with food
int check_collision(Snake* snake, Food* food) {
    if (snake[0].x == food->x && snake[0].y == food->y) {
        return 1;
    }
    return 0;
}

// Function to check collision with wall
int check_collision_wall(Snake* snake) {
    if (snake[0].x < 0 || snake[0].x >= SCREEN_WIDTH / BLOCK_SIZE || snake[0].y < 0 || snake[0].y >= SCREEN_HEIGHT / BLOCK_SIZE) {
        return 1;
    }
    return 0;
}

// Function to check collision with itself
int check_collision_self(Snake* snake, int length) {
    for (int i = 1; i < length; i++) {
        if (snake[0].x == snake[i].x && snake[0].y == snake[i].y) {
            return 1;
        }
    }
    return 0;
}

int main() {
    // Initialize SDL
    initSDL();

    // Create window
    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    if (window == NULL) {
        printf("Window could not be created! SDL_Error: %s\n", SDL_GetError());
        exit(1);
    }

    // Create renderer
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    if (renderer == NULL) {
        printf("Renderer could not be created! SDL_Error: %s\n", SDL_GetError());
        exit(1);
    }

    // Load font
    TTF_Init();
    TTF_Font* font = TTF_OpenFont("arial.ttf", 24);
    if (font == NULL) {
        printf("Font could not be loaded! SDL_ttf Error: %s\n", TTF_GetError());
        exit(1);
    }

    // Initialize snake and food
    Snake snake[100];
    Food food;
    food.x = rand() % (SCREEN_WIDTH / BLOCK_SIZE);
    food.y = rand() % (SCREEN_HEIGHT / BLOCK_SIZE);
    snake[0].x = SCREEN_WIDTH / 2 / BLOCK_SIZE;
    snake[0].y = SCREEN_HEIGHT / 2 / BLOCK_SIZE;
    int length = 1;
    int direction = 1;

    // Main loop
    int running = 1;
    SDL_Event event;
    while (running) {
        while (SDL_PollEvent(&event)) {
            handle_events(&event, snake, &food, &length, &direction);
        }

        // Update snake position
        update_snake(snake, &length, direction);

        // Check collision with wall
        if (check_collision_wall(snake)) {
            running = 0;
        }

        // Check collision with itself
        if (check_collision_self(snake, length)) {
            running = 0;
        }

        // Check collision with food
        if (check_collision(snake, &food)) {
            length++;
            food.x = rand() % (SCREEN_WIDTH / BLOCK_SIZE);
            food.y = rand() % (SCREEN_HEIGHT / BLOCK_SIZE);
        }

        // Draw everything
        SDL_SetRenderDrawColor(renderer, black.r, black.g, black.b, black.a);
        SDL_RenderClear(renderer);
        draw_snake(renderer, snake, length);
        draw_food(renderer, food);
        SDL_RenderPresent(renderer);

        // Cap framerate
        SDL_Delay(1000 / FPS);
    }

    // Clean up
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    TTF_Quit();
    SDL_Quit();

    return 0;
}
