    std::string html;
    bool in_code = false;
    bool in_link = false;
    std::string link;
    for (size_t i = 0; i < markdown.size(); ++i) {
        if (markdown[i] == '[') {
            in_link = true;
            link.clear();
        } else if (markdown[i] == ']') {
            in_link = false;
            html += "<a href=\"";
