            }
        } else if (markdown[i] == '_') {
            if (i > 0 && markdown[i - 1] != '_') {
                html += "<em>";
            } else {
                html += "</em>";
            }
        } else if (markdown[i] == '\n') {
            if (html.back() == '\n') {
                html += "<br>\n";
            } else {
                html += "\n";
            }
