#include <iostream>
#include <string>
#include <cmark.h>

std::string convertMarkdownToHTML(const std::string& markdown) {
    // Convert the input string to a cmark_input object
    cmark_input input;
    input.source = (unsigned char*)markdown.c_str();
    input.source_size = markdown.size();
    input.options = 0;

    // Create a cmark_parser object
    cmark_parser *parser = cmark_parser_new();
    cmark_parser_feed(parser, &input);

    // Convert the parsed Markdown to HTML
    std::string html;
    cmark_parser_render_html(parser, &html, 0);

    // Clean up
    cmark_parser_free(parser);

    return html;
}

int main() {
