#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>
#include <stdio.h>
#include <stdlib.h>

#define WINDOW_WIDTH 800
#define WINDOW_HEIGHT 600
#define TILE_SIZE 32

SDL_Window *window = NULL;
SDL_Renderer *renderer = NULL;
SDL_Texture *snakeTexture = NULL;
SDL_Texture *foodTexture = NULL;
