        return -1;
    }

    window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_UNDEFINED, SDL_WINDOWPOS_UNDEFINED, WINDOW_WIDTH, WINDOW_HEIGHT, SDL_WINDOW_SHOWN);
    if (window == NULL) {
        printf("Window could not be created! SDL_Error: %s\n", SDL_GetError());
        return -1;
    }

    renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    if (renderer == NULL) {
        printf("Renderer could not be created! SDL_Error: %s\n", SDL_GetError());
        return -1;
    }

    // Load textures
    snakeTexture = IMG_LoadTexture(renderer, "snake.png");
    if (snakeTexture == NULL) {
        printf("Unable to load snake texture! SDL_Error: %s\n", IMG_GetError());
        return -1;
    }

    foodTexture = IMG_LoadTexture(renderer, "food.png");
    if (foodTexture == NULL) {
        printf("Unable to load food texture! SDL_Error: %s\n", IMG_GetError());
        return -1;
    }

    // Initialize snake and food positions
    snakeRect.x = WINDOW_WIDTH / 2;
    snakeRect.y = WINDOW_HEIGHT / 2;
    snakeRect.w = TILE_SIZE;
