void game_loop(SDL_Renderer* renderer, Snake* snake, Food* food, Background* background) {
    int running = 1;
    SDL_Event event;

    while (running) {
        // Handle events
        while (SDL_PollEvent(&event)) {
            if (event.type == SDL_QUIT) {
                running = 0;
            } else if (event.type == SDL_KEYDOWN) {
                // Handle keyboard input
                if (event.key.keysym.sym == SDLK_LEFT) {
                    snake_move(snake, LEFT);
                } else if (event.key.keysym.sym == SDLK_RIGHT) {
                    snake_move(snake, RIGHT);
                } else if (event.key.keysym.sym == SDLK_UP) {
                    snake_move(snake, UP);
                } else if (event.key.keysym.sym == SDLK_DOWN) {
                    snake_move(snake, DOWN);
                }
            }
        }

        // Update game state
        snake_update(snake);
        food_update(food);
        background_update(background);

        // Render game
        render_game(renderer, snake, food, background);

        // Cap framerate
        SDL_Delay(1000 / 60);
    }
}
