                reason: reason || 'No reason provided',
            });
            await interaction.reply({
                content: `Member banned!`,
                ephemeral: false,
            });
        } catch (error) {
            console.error(error);
            await interaction.reply({
                content: 'An error occurred while banning the member!',
                ephemeral: true,
            });
        }
    }
});

