      await member.ban({ reason: reason || 'No reason provided' });
      await interaction.reply({
        content: `Member ${member.user.tag} has been banned.`,
        ephemeral: false,
      });
    } catch (error) {
      console.error(error);
      await interaction.reply({
        content: 'An error occurred while banning the member.',
        ephemeral: true,
      });
    }
  }
});

// Login to Discord with your bot token
client.login(token);
```

