// Define the game constants
#define SCREEN_WIDTH 640
#define SCREEN_HEIGHT 480
#define BLOCK_SIZE 20

// Define the snake and food structures
typedef struct {
    int x, y;
    int length;
} Snake;

typedef struct {
    int x, y;
} Food;

// Function prototypes
