#include "snake.h"
#include <stdio.h>
#include <stdlib.h>

// Initialize the game
void init_game(SDL_Window* window, SDL_Renderer* renderer) {
    // Initialize the snake and food
    snake.x = SCREEN_WIDTH / 2;
    snake.y = SCREEN_HEIGHT / 2;
    snake.length = 1;
    food.x = rand() % (SCREEN_WIDTH / BLOCK_SIZE) * BLOCK_SIZE;
    food.y = rand() % (SCREEN_HEIGHT / BLOCK_SIZE) * BLOCK_SIZE;
}

// Update the game state
void update_game(Snake* snake, Food* food) {
    // Update the snake position
    snake.x += 10;
    snake.y += 10;

    // Check for collision with food
    if (snake.x == food.x && snake.y == food.y) {
        // Increase the snake length
        snake.length++;
        // Generate new food position
        food.x = rand() % (SCREEN_WIDTH / BLOCK_SIZE) * BLOCK_SIZE;
        food.y = rand() % (SCREEN_HEIGHT / BLOCK_SIZE) * BLOCK_SIZE;
    }
}

// Render the game
void render_game(SDL_Renderer* renderer, Snake* snake, Food* food) {
    // Clear the screen
    SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
    SDL_RenderClear(renderer);

    // Draw the snake
    for (int i = 0; i < snake.length; i++) {
        SDL_Rect rect = { snake.x + i * BLOCK_SIZE, snake.y, BLOCK_SIZE, BLOCK_SIZE };
        SDL_SetRenderDrawColor(renderer, 255, 255, 255, 255);
        SDL_RenderFillRect(renderer, &rect);
    }

    // Draw the food
    SDL_Rect rect = { food.x, food.y, BLOCK_SIZE, BLOCK_SIZE };
    SDL_SetRenderDrawColor(renderer, 255, 0, 0, 255);
    SDL_RenderFillRect(renderer, &rect);

    // Update the screen
    SDL_RenderPresent(renderer);
}
```

**`main.c`**
```c
#include "snake.h"
#include <SDL2/SDL.h>

int main() {
    // Initialize SDL
    SDL_Init(SDL_INIT_VIDEO);

    // Create a window
    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);

    // Create a renderer
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);

    // Initialize the game
    Snake snake;
    Food food;
    init_game(window, renderer);

    // Game loop
    while (1) {
        // Update the game state
        update_game(&snake, &food);

        // Render the game
        render_game(renderer, &snake, &food);

        // Handle events
        SDL_Event event;
        while (SDL_PollEvent(&event)) {
            if (event.type == SDL_QUIT) {
                break;
            }
        }

        // Cap the frame rate
        SDL_Delay(1000 / 60);
    }

    // Clean up
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();

    return 0;
}
```

