#include <pugixml.hpp>
#include <string>

/**
 * Converts markdown formatted text to HTML.
 *
 * @param markdown The markdown formatted text to convert.
 * @return The HTML formatted text.
 */
std::string markdownToHtml(const std::string& markdown) {
    pugi::xml_document doc;
    pugi::xml_parse_result result = doc.load_string(markdown.c_str());

    if (!result) {
        // Handle parsing errors
        return "";
    }

    pugi::xml_node root = doc.document_element();
    std::string html;

    // Convert markdown to HTML
    for (pugi::xml_node node = root.first_child(); node; node = node.next_sibling()) {
        if (node.type() == pugi::node_element) {
            pugi::xml_node child = node.first_child();
            while (child) {
                if (child.type() == pugi::node_text) {
                    // Convert markdown text to HTML
                    std::string text = child.value();
                    // Replace markdown syntax with HTML
                    // For example:
                    //   - [text](url) -> <a href="url">text</a>
                    //   **bold text** -> <b>bold text</b>
                    //   *italic text* -> <i>italic text</i>
                    // ...
                    // Implement your markdown to HTML conversion logic here
                    text = convertMarkdownToHtml(text);
                    child.text().set(text.c_str());
                }
                child = child.next_sibling();
            }
        }
    }

    // Convert the XML document to a string
    pugi::xml_writer_settings settings;
    pugi::xml_print_settings printSettings;
    printSettings.m_indent = 0;
    printSettings.m_indentation = 0;
    pugi::xml_writer writer(&html, settings, printSettings);

    doc.save(writer);

    return html;
}

// Example implementation of markdown to HTML conversion
std::string convertMarkdownToHtml(const std::string& markdown) {
    // Implement your markdown to HTML conversion logic here
    // For example:
    if (markdown.find("[") == 0 && markdown.find("]") == markdown.size() - 1) {
        size_t start = markdown.find("(");
        size_t end = markdown.find(")");
        if (start != std::string::npos && end != std::string::npos) {
            std::string link = markdown.substr(start + 1, end - start - 1);
            return "<a href=\"" + link + "\">" + markdown.substr(1, start - 1) + "</a>";
        }
    } else if (markdown.find("**") == 0 && markdown.find("**") == markdown.size() - 2) {
        return "<b>" + markdown.substr(2, markdown.size() - 4) + "</b>";
    } else if (markdown.find("*") == 0 && markdown.find("*") == markdown.size() - 1) {
        return "<i>" + markdown.substr(1, markdown.size() - 2) + "</i>";
    }

    return markdown;
