#include <SDL2/SDL_image.h>
#include <stdio.h>
#include <stdlib.h>

// Define constants
#define SCREEN_WIDTH 800
#define SCREEN_HEIGHT 600
#define BLOCK_SIZE 20

// Define colors
SDL_Color white = {255, 255, 255};
SDL_Color black = {0, 0, 0};

// Define the snake structure
typedef struct {
    int x, y;
    int length;
    int direction;
} Snake;

// Define the food structure
typedef struct {
    int x, y;
} Food;

