void initSDL() {
    if (SDL_Init(SDL_INIT_VIDEO) < 0) {
        printf("SDL initialization failed: %s\n", SDL_GetError());
        exit(1);
    }
}

// Function to load an image
SDL_Texture* loadTexture(const char* filename) {
    SDL_Surface* surface = IMG_Load(filename);
    if (!surface) {
        printf("Failed to load image: %s\n", IMG_GetError());
        exit(1);
    }
    SDL_Texture* texture = SDL_CreateTextureFromSurface(SDL_GetRenderer(SDL_GetWindowFromID(SDL_GetWindowID(NULL))), surface);
    SDL_FreeSurface(surface);
    return texture;
}

// Function to render the game
void renderGame(SDL_Renderer* renderer, Snake snake, Food food) {
    // Clear the screen
    SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
    SDL_RenderClear(renderer);

    // Draw the snake
    for (int i = 0; i < snake.length; i++) {
        SDL_Rect rect = {snake.x + i * BLOCK_SIZE, snake.y, BLOCK_SIZE, BLOCK_SIZE};
        SDL_SetRenderDrawColor(renderer, 255, 0, 0, 255);
        SDL_RenderFillRect(renderer, &rect);
    }

    // Draw the food
    SDL_Rect foodRect = {food.x, food.y, BLOCK_SIZE, BLOCK_SIZE};
    SDL_SetRenderDrawColor(renderer, 0, 255, 0, 255);
    SDL_RenderFillRect(renderer, &foodRect);

    // Update the screen
    SDL_RenderPresent(renderer);
}

// Function to handle events
