    if (event->type == SDL_QUIT) {
        // Handle quit event
    } else if (event->type == SDL_KEYDOWN) {
        // Handle key press
        switch (event->key.keysym.sym) {
            case SDLK_UP:
                snake->direction = 0;
                break;
            case SDLK_DOWN:
                snake->direction = 1;
                break;
            case SDLK_LEFT:
                snake->direction = 2;
                break;
            case SDLK_RIGHT:
                snake->direction = 3;
                break;
        }
    }
}

// Function to update the game state
