    // Update snake position
    switch (snake->direction) {
        case 0:
            snake->y -= BLOCK_SIZE;
            break;
        case 1:
            snake->y += BLOCK_SIZE;
            break;
        case 2:
            snake->x -= BLOCK_SIZE;
            break;
        case 3:
            snake->x += BLOCK_SIZE;
