const showHistoryButton = document.getElementById("show-history-button");
const chatHistoryContainer = document.getElementById("chat-history-container");
const chatHistoryList = document.getElementById("chat-history-list");

// Add event listeners to the chat form and show history button
chatForm.addEventListener("submit", handleChatFormSubmit);
showHistoryButton.addEventListener("click", handleShowHistoryButtonClick);

// Function to handle the chat form submission
function handleChatFormSubmit(event) {
  event.preventDefault();

  // Get the message from the input
  const message = messageInput.value;

  // Add the message to the chat history list
  addMessageToChatHistoryList(message, "user-message");

  // Clear the input field
  messageInput.value = "";
}

// Function to handle the show history button click
function handleShowHistoryButtonClick() {
  // Toggle the visibility of the chat history modal
  chatHistoryModal.classList.toggle("visible");
}

// Function to add a message to the chat history list
function addMessageToChatHistoryList(message, className) {
  // Create a new list item element
  const listItem = document.createElement("li");

  // Set the class attribute of the list item
  listItem.classList.add(className);

  // Add the message text to the list item
  listItem.textContent = message;

  // Add the list item to the chat history list
  chatHistoryList.appendChild(listItem);
}
