#include <regex>

std::string markdownToHtml(const std::string& markdown) {
    std::string html;

    // Replace Markdown headers with HTML headers
    html = std::regex_replace(markdown, std::regex("^(#+) (.*)$"), "<h$1>$2</h$1>");

    // Replace Markdown emphasis with HTML emphasis
    html = std::regex_replace(html, std::regex("(?<!\*)\*(.*?)(?!\*)\*"), "<em>$1</em>");

    // Replace Markdown links with HTML links
    html = std::regex_replace(html, std::regex("\\[(.*?)\\]\\((.*?)\\)"), "<a href=\"$2\">$1</a>");

    // Replace Markdown lists with HTML lists
    html = std::regex_replace(html, std::regex("^ *([*+-]) +(.*)$"), "<li>$3</li>");

    return html;
}
