#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>

#define CELL_SIZE 32
#define BOARD_WIDTH 32
#define BOARD_HEIGHT 24

typedef struct {
    int x;
    int y;
} Cell;

typedef struct {
    Cell head;
    Cell tail;
    int length;
} Snake;

