    SDL_Window *window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_UNDEFINED, SDL_WINDOWPOS_UNDEFINED, BOARD_WIDTH * CELL_SIZE, BOARD_HEIGHT * CELL_SIZE, SDL_WINDOW_SHOWN);
