    // Respond to the user
    await interaction.reply(`User with ID ${userId} has been banned`);
  },
};

// Register the ban command
client.commands = new Collection();
client.commands.set(banCommand.name, banCommand);

// Register the command with the Discord API
(async () => {
  try {
    await rest.put(Routes.applicationCommands(YOUR_BOT_TOKEN), {
      body: client.commands,
    });

    console.log("Successfully registered commands");
  } catch (error) {
    console.error(error);
  }
})();

// Start the client
client.login(YOUR_BOT_TOKEN);
