class Snake {
public:
    Snake() {
        // Initialize snake position and direction
        x = 0;
        y = 0;
        direction = 0;
    }

    void move() {
        // Update snake position based on direction
        switch (direction) {
            case 0:
                x++;
                break;
            case 1:
                y++;
                break;
            case 2:
                x--;
                break;
            case 3:
                y--;
                break;
        }
    }

