```
#include <iostream>
#include <fstream>
#include <string>
using namespace std;
int main() {
  string word; // word to search for
  int count = 0; // count of occurrences
  cout << "Enter the word to search for: ";
  cin >> word;
  ifstream infile("textfile.txt"); // open file
  if (infile) {
    string line; // current line
    while (getline(infile, line)) { // read lines
      size_t pos = 0; // position in line
      while ((pos = line.find(word, pos)) != string::npos) { // search for word
        count++; // increment count
        pos += word.length(); // move to next position
      }
    }
    infile.close(); // close file
  }
  else {
    cout << "Error opening file." << endl;
    return 1;
  }
  cout << "The word '" << word << "' occurs " << count << " times." << endl;
  return 0;
}
```
