import logging

def log(func):
    def wrapper(*args, **kwargs):
        logging.info(f"Calling {func.__name__}")
        return func(*args, **kwargs)
    return wrapper

@log
def add(x, y):
    return x + y

print(add(2, 3))  # Output: INFO:root:Calling add
