#define BLOCK_SIZE 20
#define MAX_SNAKE_LENGTH 100

// Define the snake's initial position and direction
int snake_x = SCREEN_WIDTH / 2;
int snake_y = SCREEN_HEIGHT / 2;
int snake_direction = 0;

// Define the food's position
int food_x;
int food_y;

