  return game_state;
}

// Define the game loop
int game_loop() {
  // Initialize the game state
  game_state = 0;

  // Initialize the snake's body
  int snake_length = 1;
  snake_body[0][0] = snake_x;
  snake_body[0][1] = snake_y;

  // Generate the food
