  // Set the game loop speed
  int delay = 100;

  // Set the game loop running
  while (game_state == 0) {
    // Handle input
    handle_input();

    // Update the snake's position and direction
    update();

    // Check for collisions
    check_collisions();

    // Draw the game board
    draw();

    // Update the screen
    SDL_RenderPresent(renderer);

    // Wait for the delay
    SDL_Delay(delay);
  }

  // Return the game state
  return game_state;
}

