  SDL_FreeSurface(background_surface);
  SDL_RenderCopy(renderer, background_texture, NULL, NULL);
  SDL_DestroyTexture(background_texture);

  // Draw the food image
  SDL_Surface* food_surface = SDL_LoadBMP("food.bmp");
  SDL_Texture* food_texture = SDL_CreateTextureFromSurface(renderer, food_surface);
  SDL_FreeSurface(food_surface);
  SDL_RenderCopy(renderer, food_texture, NULL, NULL);
  SDL_DestroyTexture(food_texture);

  // Draw the snake's body
  for (int i = 0; i < snake_length; i++) {
    SDL_Surface* snake_surface = SDL_LoadBMP("snake.bmp");
    SDL_Texture* snake_texture = SDL_CreateTextureFromSurface(renderer, snake_surface);
    SDL_FreeSurface(snake_surface);
    SDL_Rect snake_rect = {snake_body[i][0] * BLOCK_SIZE, snake_body[i][1] * BLOCK_SIZE, BLOCK_SIZE, BLOCK_SIZE};
    SDL_RenderCopy(renderer, snake_texture, NULL, &snake_rect);
    SDL_DestroyTexture(snake_texture);
  }
}

// Define the update function
void update() {
  // Update the snake's position and direction
  switch (snake_direction) {
    case 0:
      snake_y -= BLOCK_SIZE;
      break;
    case 1:
      snake_x += BLOCK_SIZE;
      break;
    case 2:
      snake_y += BLOCK_SIZE;
      break;
    case 3:
      snake_x -= BLOCK_SIZE;
      break;
  }

  // Update the snake's body
  for (int i = snake_length - 1; i > 0; i--) {
    snake_body[i][0] = snake_body[i - 1][0];
    snake_body[i][1] = snake_body[i - 1][1];
  }

  // Update the snake's head
  snake_body[0][0] = snake_x;
  snake_body[0][1] = snake_y;

  // Check for collisions with the walls
  if (snake_x < 0 || snake_x >= SCREEN_WIDTH || snake_y < 0 || snake_y >= SCREEN_HEIGHT) {
    game_state = 1;
  }
}

// Define the handle_input function
void handle_input() {
  // Handle keyboard input
  SDL_Event event;
  while (SDL_PollEvent(&event)) {
    if (event.type == SDL_QUIT) {
      game_state = 1;
    } else if (event.type == SDL_KEYDOWN) {
      switch (event.key.keysym.sym) {
        case SDLK_UP:
          if (snake_direction != 2) {
            snake_direction = 0;
          }
          break;
        case SDLK_RIGHT:
          if (snake_direction != 3) {
            snake_direction = 1;
          }
          break;
        case SDLK_DOWN:
          if (snake_direction != 0) {
            snake_direction = 2;
          }
          break;
        case SDLK_LEFT:
          if (snake_direction != 1) {
            snake_direction = 3;
          }
          break;
      }
    }
  }
}

// Define the check_collisions function
void check_collisions() {
  // Check for collisions with the snake's body
  for (int i = 1; i < snake_length; i++) {
    if (snake_x == snake_body[i][0] && snake_y == snake_body[i][1]) {
      game_state = 1;
    }
  }

  // Check for collisions with the food
  if (snake_x == food_x && snake_y == food_y) {
    // Increase the snake's length
    snake_length++;

    // Generate new food
    generate_food();

    // Increase the score
    score++;
  }
}

// Define the generate_food function
void generate_food() {
  // Generate a random food position
  food_x = rand() % (SCREEN_WIDTH / BLOCK_SIZE) * BLOCK_SIZE;
  food_y = rand() % (SCREEN_HEIGHT / BLOCK_SIZE) * BLOCK_SIZE;

  // Check for collisions with the snake's body
  for (int i = 0; i < snake_length; i++) {
    if (food_x == snake_body[i][0] && food_y == snake_body[i][1]) {
      generate_food();
    }
  }
}
