#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>

#define SCREEN_WIDTH 640
#define SCREEN_HEIGHT 480
#define CELL_SIZE 20
#define NUM_CELLS_X (SCREEN_WIDTH / CELL_SIZE)
#define NUM_CELLS_Y (SCREEN_HEIGHT / CELL_SIZE)

// Game board
int board[NUM_CELLS_X][NUM_CELLS_Y];

// Snake
int snake_x = NUM_CELLS_X / 2;
int snake_y = NUM_CELLS_Y / 2;
int snake_dir = 0; // 0 = up, 1 = right, 2 = down, 3 = left

// Food
int food_x = 0;
int food_y = 0;

// Game state
int game_over = 0;

// Images
SDL_Surface *snake_img;
SDL_Surface *food_img;
SDL_Surface *background_img;

// SDL initialization
SDL_Window *window;
SDL_Renderer *renderer;

// Function prototypes
void init_game();
void update_game();
void draw_game();
void handle_input();
void generate_food();
void check_game_over();

int main(int argc, char *argv[]) {
  // Initialize SDL
  if (SDL_Init(SDL_INIT_VIDEO) < 0) {
    printf("SDL initialization failed: %s\n", SDL_GetError());
    return 1;
  }

  // Create window
  window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
  if (window == NULL) {
    printf("Window creation failed: %s\n", SDL_GetError());
    return 1;
  }

  // Create renderer
  renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED | SDL_RENDERER_PRESENTVSYNC);
  if (renderer == NULL) {
    printf("Renderer creation failed: %s\n", SDL_GetError());
    return 1;
  }

  // Load images
  snake_img = IMG_Load("snake.png");
  food_img = IMG_Load("food.png");
  background_img = IMG_Load("background.png");

  // Initialize game
  init_game();

  // Main game loop
  while (!game_over) {
    // Handle input
    handle_input();

    // Update game
    update_game();

    // Draw game
    draw_game();

    // Check for game over
    check_game_over();
  }

  // Clean up
  SDL_DestroyRenderer(renderer);
  SDL_DestroyWindow(window);
  SDL_Quit();

  return 0;
}

void init_game() {
  // Initialize game board
  for (int i = 0; i < NUM_CELLS_X; i++) {
    for (int j = 0; j < NUM_CELLS_Y; j++) {
      board[i][j] = 0;
    }
  }

  // Initialize snake
  snake_x = NUM_CELLS_X / 2;
  snake_y = NUM_CELLS_Y / 2;
  board[snake_x][snake_y] = 1;

  // Generate food
  generate_food();
}

void update_game() {
  // Move snake
  int new_x = snake_x;
  int new_y = snake_y;

  switch (snake_dir) {
    case 0: // Up
      new_y--;
      break;
    case 1: // Right
      new_x++;
      break;
    case 2: // Down
      new_y++;
      break;
    case 3: // Left
      new_x--;
      break;
  }

  // Check for collisions
  if (new_x < 0 || new_x >= NUM_CELLS_X || new_y < 0 || new_y >= NUM_CELLS_Y || board[new_x][new_y] == 1) {
    game_over = 1;
  } else {
    // Update snake position
    board[snake_x][snake_y] = 0;
    snake_x = new_x;
    snake_y = new_y;
    board[snake_x][snake_y] = 1;

    // Check for food
    if (snake_x == food_x && snake_y == food_y) {
      generate_food();
    }
  }
}

void draw_game() {
  // Clear screen
  SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
  SDL_RenderClear(renderer);

  // Draw background
  SDL_Rect background_rect = {0, 0, SCREEN_WIDTH, SCREEN_HEIGHT};
  SDL_RenderCopy(renderer, background_img, NULL, &background_rect);

  // Draw snake
  for (int i = 0; i < NUM_CELLS_X; i++) {
    for (int j = 0; j < NUM_CELLS_Y; j++) {
      if (board[i][j] == 1) {
        SDL_Rect snake_rect = {i * CELL_SIZE, j * CELL_SIZE, CELL_SIZE, CELL_SIZE};
        SDL_RenderCopy(renderer, snake_img, NULL, &snake_rect);
      }
    }
  }

  // Draw food
  SDL_Rect food_rect = {food_x * CELL_SIZE, food_y * CELL_SIZE, CELL_SIZE, CELL_SIZE};
  SDL_RenderCopy(renderer, food_img, NULL, &food_rect);

  // Update screen
  SDL_RenderPresent(renderer);
}

void handle_input() {
  SDL_Event event;
  while (SDL_PollEvent(&event)) {
    if (event.type == SDL_QUIT) {
      game_over = 1;
    } else if (event.type == SDL_KEYDOWN) {
      switch (event.key.keysym.sym) {
        case SDLK_UP:
          if (snake_dir != 2) {
            snake_dir = 0;
          }
          break;
        case SDLK_RIGHT:
          if (snake_dir != 3) {
            snake_dir = 1;
          }
          break;
        case SDLK_DOWN:
          if (snake_dir != 0) {
            snake_dir = 2;
          }
          break;
        case SDLK_LEFT:
          if (snake_dir != 1) {
            snake_dir = 3;
          }
          break;
      }
    }
  }
}

void generate_food() {
  // Generate random food position
  food_x = rand() % NUM_CELLS_X;
  food_y = rand() % NUM_CELLS_Y;

  // Check if food position is valid
  while (board[food_x][food_y] == 1) {
    food_x = rand() % NUM_CELLS_X;
    food_y = rand() % NUM_CELLS_Y;
  }

  // Set food position on game board
  board[food_x][food_y] = 2;
}

void check_game_over() {
  if (game_over) {
    SDL_Delay(1000);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();
    exit(0);
  }
}
