int state;
SDL_Window *window;
SDL_Renderer *renderer;
SDL_Surface *snake_image, *food_image, *board_image;
SDL_Texture *snake_texture, *food_texture, *board_texture;

// Function prototypes
void init_game();
void update_game();
void render_game();
void handle_input();
void handle_collisions();
void generate_food();
void game_over();

int main(int argc, char *argv[]) {
    // Initialize SDL
    if (SDL_Init(SDL_INIT_VIDEO) < 0) {
        printf("SDL could not initialize! SDL_Error: %s\n", SDL_GetError());
        return 1;
    }

    // Create window
    window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    if (window == NULL) {
        printf("Window could not be created! SDL_Error: %s\n", SDL_GetError());
        return 1;
    }

    // Create renderer
    renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    if (renderer == NULL) {
        printf("Renderer could not be created! SDL_Error: %s\n", SDL_GetError());
        return 1;
    }

    // Load images
    snake_image = IMG_Load("snake.png");
    food_image = IMG_Load("food.png");
    board_image = IMG_Load("board.png");

    // Convert images to textures
    snake_texture = SDL_CreateTextureFromSurface(renderer, snake_image);
    food_texture = SDL_CreateTextureFromSurface(renderer, food_image);
    board_texture = SDL_CreateTextureFromSurface(renderer, board_image);

    // Initialize game
    init_game();

    // Main game loop
    while (state == STATE_RUNNING) {
        // Handle input
        handle_input();

        // Update game
        update_game();

        // Render game
        render_game();

        // Handle collisions
        handle_collisions();

        // Generate food
        generate_food();

        // Check for game over
        if (state == STATE_GAME_OVER) {
            game_over();
        }
    }

    // Clean up
    SDL_DestroyTexture(snake_texture);
    SDL_DestroyTexture(food_texture);
    SDL_DestroyTexture(board_texture);
    SDL_FreeSurface(snake_image);
    SDL_FreeSurface(food_image);
    SDL_FreeSurface(board_image);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();

    return 0;
}

void init_game() {
    // Initialize snake
    snake.x = SCREEN_WIDTH / 2;
    snake.y = SCREEN_HEIGHT / 2;
    snake.dir_x = 0;
    snake.dir_y = -1;

    // Initialize food
    food_x = rand() % (SCREEN_WIDTH / BLOCK_SIZE) * BLOCK_SIZE;
    food_y = rand() % (SCREEN_HEIGHT / BLOCK_SIZE) * BLOCK_SIZE;

    // Initialize score
    score = 0;

    // Initialize state
    state = STATE_RUNNING;
}

void update_game() {
    // Move snake
    snake.x += snake.dir_x * BLOCK_SIZE;
    snake.y += snake.dir_y * BLOCK_SIZE;

    // Check for game over
    if (snake.x < 0 || snake.x >= SCREEN_WIDTH || snake.y < 0 || snake.y >= SCREEN_HEIGHT) {
        state = STATE_GAME_OVER;
    }
}

void render_game() {
    // Clear screen
    SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
    SDL_RenderClear(renderer);

    // Draw board
    SDL_Rect board_rect = {0, 0, SCREEN_WIDTH, SCREEN_HEIGHT};
    SDL_RenderCopy(renderer, board_texture, NULL, &board_rect);

    // Draw snake
    SDL_Rect snake_rect = {snake.x, snake.y, BLOCK_SIZE, BLOCK_SIZE};
    SDL_RenderCopy(renderer, snake_texture, NULL, &snake_rect);

    // Draw food
    SDL_Rect food_rect = {food_x, food_y, BLOCK_SIZE, BLOCK_SIZE};
    SDL_RenderCopy(renderer, food_texture, NULL, &food_rect);

    // Draw score
    char score_text[32];
    sprintf(score_text, "Score: %d", score);
    SDL_Surface *score_surface = TTF_RenderText_Solid(g_font, score_text, g_color);
    SDL_Texture *score_texture = SDL_CreateTextureFromSurface(renderer, score_surface);
    SDL_Rect score_rect = {10, 10, 100, 50};
    SDL_RenderCopy(renderer, score_texture, NULL, &score_rect);
    SDL_FreeSurface(score_surface);
    SDL_DestroyTexture(score_texture);

    // Update screen
    SDL_RenderPresent(renderer);
}

void handle_input() {
    SDL_Event event;
    while (SDL_PollEvent(&event)) {
        if (event.type == SDL_QUIT) {
            state = STATE_GAME_OVER;
        } else if (event.type == SDL_KEYDOWN) {
            switch (event.key.keysym.sym) {
                case SDLK_UP:
                    if (snake.dir_y != 1) {
                        snake.dir_x = 0;
                        snake.dir_y = -1;
                    }
                    break;
                case SDLK_DOWN:
                    if (snake.dir_y != -1) {
                        snake.dir_x = 0;
                        snake.dir_y = 1;
                    }
                    break;
                case SDLK_LEFT:
                    if (snake.dir_x != 1) {
                        snake.dir_x = -1;
                        snake.dir_y = 0;
                    }
                    break;
                case SDLK_RIGHT:
                    if (snake.dir_x != -1) {
                        snake.dir_x = 1;
                        snake.dir_y = 0;
                    }
                    break;
            }
        }
    }
}

void handle_collisions() {
    // Check for collision with food
    if (snake.x == food_x && snake.y == food_y) {
        // Generate new food
        generate_food();

        // Increase score
        score++;

        // Increase snake length
        snake.dir_x = 0;
        snake.dir_y = 0;
    }
}

void generate_food() {
    // Generate new food
    food_x = rand() % (SCREEN_WIDTH / BLOCK_SIZE) * BLOCK_SIZE;
    food_y = rand() % (SCREEN_HEIGHT / BLOCK_SIZE) * BLOCK_SIZE;
}

void game_over() {
    // Display game over screen
    SDL_Surface *game_over_surface = TTF_RenderText_Solid(g_font, "Game Over", g_color);
    SDL_Texture *game_over_texture = SDL_CreateTextureFromSurface(renderer, game_over_surface);
    SDL_Rect game_over_rect = {SCREEN_WIDTH / 2 - 100, SCREEN_HEIGHT / 2 - 50, 200, 100};
    SDL_RenderCopy(renderer, game_over_texture, NULL, &game_over_rect);
    SDL_FreeSurface(game_over_surface);
    SDL_DestroyTexture(game_over_texture);

    // Wait for user input
    SDL_Event event;
    while (SDL_PollEvent(&event)) {
        if (event.type == SDL_QUIT) {
            break;
        } else if (event.type == SDL_KEYDOWN) {
            init_game();
            break;
        }
    }
}
