```cpp
#include <pugixml.hpp>
#include <string>

std::string markdown_to_html(const std::string& markdown_text) {
    pugi::xml_document doc;
    pugi::xml_parse_result result = doc.load_string(markdown_text.c_str());

    if (!result) {
        // Handle parsing error
        return "";
    }

    pugi::xml_node root = doc.document_element();
    std::string html_text;

    // Convert markdown to HTML
    for (pugi::xml_node node = root.first_child(); node; node = node.next_sibling()) {
        if (node.type() == pugi::node_element) {
            pugi::xml_node child = node.first_child();
            while (child) {
                if (child.type() == pugi::node_text) {
                    std::string text = child.text().as_string();
                    // Convert markdown text to HTML
                    // This is a simplified example and does not cover all markdown syntax
                    if (text.find("#") == 0) {
                        html_text += "<h" + std::to_string(text[0] - '0') + ">" + text.substr(1) + "</h" + std::to_string(text[0] - '0') + ">";
                    } else if (text.find("*") == 0 || text.find("_") == 0) {
                        html_text += "<em>" + text.substr(1) + "</em>";
                    } else if (text.find("[") == 0) {
                        size_t end = text.find("]");
                        if (end != std::string::npos) {
                            std::string link = text.substr(1, end - 1);
                            size_t space = link.find(" ");
                            if (space != std::string::npos) {
                                std::string link_text = link.substr(0, space);
                                std::string link_url = link.substr(space + 1);
                                html_text += "<a href=\"" + link_url + "\">" + link_text + "</a>";
                            }
                        }
                    }
                }
                child = child.next_sibling();
            }
        }
    }

    return html_text;
}
```

