def insertionSort[A : Type](xs : List A) : List A :=
  xs.foldl (fun x xs => List.insert x xs) []

-- Function to perform insertion sort on a sublist
def insertionSortSublist[A : Type](xs : List A, lo : Nat, hi : Nat) : List A :=
  let mid := (lo + hi) / 2;
  insertionSort (xs.take mid ++ insertionSortSublist (xs.drop mid) lo mid)

