```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <vector>

int main() {
    std::string wordToCount;
    std::string line;
    int count = 0;

    // Prompt the user for the word to count
    std::cout << "Enter the word to count: ";
    std::cin >> wordToCount;

    // Open the file in read mode
    std::ifstream inputFile("input.txt");

    if (!inputFile) {
        std::cerr << "Error: Could not open the file." << std::endl;
        return 1;
    }

    // Read the file line by line
    while (std::getline(inputFile, line)) {
        // Convert the line to lowercase to make the word case-insensitive
        std::transform(line.begin(), line.end(), line.begin(), ::tolower);
        // Count the occurrences of the word in the line
        count += std::count(line.begin(), line.end(), wordToCount);
    }

    // Close the file
    inputFile.close();

    // Output the result
    std::cout << "The word \"" << wordToCount << "\" occurs " << count << " times in the file." << std::endl;

    return 0;
}
