// Function to count the number of occurrences of a word in a vector of strings
int countWordOccurances(const std::vector<std::string>& lines, const std::string& word) {
    int count = 0;
    for (const auto& line : lines) {
        std::string::const_iterator it = line.begin();
        while (it != line.end()) {
            if (*it == word[0]) {
                std::string substr;
                substr.push_back(*it);
                if (line.substr(it, word.length()) == word) {
                    count++;
                }
                it += word.length() - 1; // Move forward by the length of the word to avoid infinite loop
            }
            it++;
        }
    }
    return count;
}

int main() {
    std::string filename = "input.txt"; // Replace with your file name
    std::string wordToCount = "example"; // Replace with the word you want to count

    std::ifstream infile(filename);
    if (!infile) {
        std::cerr << "Error: Could not open file " << filename << std::endl;
        return 1;
    }

    std::vector<std::string> lines;
    std::string line;
    while (std::getline(infile, line)) {
        lines.push_back(line);
    }
    infile.close();

    int count = countWordOccurances(lines, wordToCount);
    std::cout << "The word \"" << wordToCount << "\" occurs " << count << " times in the file." << std::endl;

    return 0;
}
```

