#include <iostream>
#include <fstream>
#include <string>

int countWordOccurences(std::string filename, std::string word) {
    std::ifstream file(filename);
    std::string line;
    int count = 0;

    while (std::getline(file, line)) {
        size_t pos = line.find(word);
        while (pos != std::string::npos) {
            count++;
            pos = line.find(word, pos + word.length());
        }
    }

    return count;
}

