// Helper function to determine if a word matches at a given position
bool isMatch(const std::string& str, size_t pos, const std::string& word) {
    return std::equal(word.begin(), word.end(), str.begin() + pos, [](char a, char b) {
        return ::tolower(a) == b || ::toupper(a) == b; // Case-insensitive comparison
    });
}

