#include <iostream>
#include <string>
#include <regex>

std::string convertMarkdownToHTML(const std::string& markdown) {
    // Define the replacements
    std::string markdownText = markdown;
    std::string htmlText;

    // Headers
    std::regex headerRegex("^#+( )*(.*)$", std::regex::icase);
    htmlText = std::regex_replace(htmlText, headerRegex, "<h\\1>\\2</h\\1>", std::regex_constants::format_first_only);

    // Emphasis
    std::regex emphasisRegex("_(.*?)_", std::regex::icase);
    htmlText = std::regex_replace(htmlText, emphasisRegex, "<em>\\1</em>");

    // Strong
    std::regex strongRegex("__(.*?)__", std::regex::icase);
    htmlText = std::regex_replace(htmlText, strongRegex, "<strong>\\1</strong>");

    // Links
    std::regex linkRegex("\[(.*?)\]\((.*?)\)");
    htmlText = std::regex_replace(htmlText, linkRegex, "<a href=\"\\2\">\\1</a>");

    // Lists
    std::regex unorderedListRegex("^ *(\\*|\\-|\\+) (.*?)$", std::regex::icase);
    htmlText = std::regex_replace(htmlText, unorderedListRegex, "<li>\\2</li>", std::regex_constants::format_first_only);
    std::regex orderedListRegex("^\\d+\\. (.*?)$", std::regex::icase);
    htmlText = std::regex_replace(htmlText, orderedListRegex, "<li>\\1</li>", std::regex_constants::format_first_only);

    // Paragraphs
    std::regex paragraphRegex("^(.*)$", std::regex::icase);
    htmlText = std::regex_replace(htmlText, paragraphRegex, "<p>\\1</p>", std::regex_constants::format_first_only);

    // Blockquotes
    std::regex blockquoteRegex("^> (.*?)$", std::regex::icase);
    htmlText = std::regex_replace(htmlText, blockquoteRegex, "<blockquote>\\1</blockquote>", std::regex_constants::format_first_only);

    // Code
    std::regex codeRegex("```(.*?)```", std::regex::icase);
    htmlText = std::regex_replace(htmlText, codeRegex, "<pre><code>\\1</code></pre>", std::regex_constants::format_first_only);

    // Images
    std::regex imageRegex("!\\[(.*?)\\]\((.*?)\)");
    htmlText = std::regex_replace(htmlText, imageRegex, "<img src=\"\\2\" alt=\"\\1\">");

    return htmlText;
}

int main() {
    std::string markdown = "# Hello\n\nThis is a [link](https://example.com).\n\n- Item 1\n- Item 2\n\n> Blockquote\n\n```\nCode\n```\n\n**Bold** and *Italic*";
    std::string html = convertMarkdownToHTML(markdown);
    std::cout << "Markdown:\n" << markdown << "\n\nHTML:\n" << html << std::endl;
    return 0;
}
```

