    cout << "Enter the name of the file: ";
    cin >> filename;
    cout << "Enter the word to count: ";
    cin >> word;

    int count = countWordOccurrences(filename, word);
    cout << "The word \"" << word << "\" occurs " << count << " times in the file." << endl;

    return 0;
}
