  html = std::regex_replace(html, std::regex("^(\\s*)\\+(\\s*)(.*)$"), "<li>\\3</li>");

  // Code
  html = std::regex_replace(html, std::regex("```(.*?)```"), "<pre><code>\\1</code></pre>");

  return html;
