  quack(): string;
}

function quack(quackable: Quackable) {
  console.log(quackable.quack());
}

class Duck implements Quackable {
  quack(): string {
    return "Quack!";
  }
}

class Turkey implements Quackable {
  quack(): string {
    return "Gobble!";
  }
