      await interaction.guild.members.ban(user, { reason });
      return interaction.reply(`User ${user.tag} has been banned.`);
    } catch (error) {
      return interaction.reply(`Failed to ban user ${user.tag}: ${error.message}`);
    }
  }
});

// Login to Discord with the token
