```cpp
#include <pugixml.hpp>
#include <string>

/**
 * Converts markdown formatted text to HTML.
 *
 * @param markdownText Reference to a std::string containing markdown text.
 * @return std::string containing the corresponding HTML text.
 */
std::string markdownToHtml(const std::string& markdownText) {
    pugi::xml_document doc;
    pugi::xml_parse_result result = doc.load_string(markdownText.c_str());

    if (!result) {
        // Handle parsing error
        return "";
    }

    pugi::xml_node root = doc.document_element();
    std::string htmlText;
    pugi::print_tree(root, htmlText);

    return htmlText;
}
